Fn::Sub:
{
  "StartAt": "IngredientPreparation",
  "States": {
    "IngredientPreparation": {
      "Type": "Task",
      "Resource": "${130c2b2a212072efa9cd377ea984b41d}",
      "Retry": [
        {
          "ErrorEquals": [
            "States.TaskFailed"
          ],
          "IntervalSeconds": 10,
          "MaxAttempts": 3
        }
      ],
      "Next": "CheckIngredients"
    },
    "CheckIngredients": {
      "Type": "Choice",
      "Choices": [
        {
          "Variable": "$.statusCode",
          "NumericEquals": 200,
          "Next": "MixingIngredients"
        }
      ],
      "Default": "InsufficientIngredients"
    },
    "InsufficientIngredients": {
      "Type": "Fail",
      "Cause": "Insufficient ingredients to proceed with the mixing process."
    },
    "MixingIngredients": {
      "Type": "Map",
      "InputPath": "$.body.ingredients",
      "Iterator": {
        "StartAt": "MixIngredient",
        "States": {
          "MixIngredient": {
            "Type": "Task",
            "ResultPath": "$.mixResult",
            "Resource": "${0d375db84962cf0b6a4cd860bb8c4267}",
            "Next": "WaitForMixing"
          },
          "WaitForMixing": {
            "Type": "Wait",
            "SecondsPath": "$.mixResult.body.waitTime",
            "End": true
          }
        }
      },
      "Next": "Shaping"
    },
    "Shaping": {
      "Type": "Task",
      "Resource": "${082b03f3d8bba083138c01073c0c66be}",
      "Retry": [
        {
          "ErrorEquals": [
            "States.TaskFailed"
          ],
          "IntervalSeconds": 10,
          "MaxAttempts": 3
        }
      ],
      "Next": "QualityCheck"
    },
    "QualityCheck": {
      "Type": "Parallel",
      "Branches": [
        {
          "StartAt": "CheckShape",
          "States": {
            "CheckShape": {
              "Type": "Task",
              "ResultPath": "$.checkShape",
              "Resource": "${b7159b2a7977854b46aea1ab1c0c9796}",
              "Retry": [
                {
                  "ErrorEquals": [
                    "States.TaskFailed"
                  ],
                  "IntervalSeconds": 10,
                  "MaxAttempts": 3
                }
              ],
              "End": true
            }
          }
        },
        {
          "StartAt": "ApiQualityCheck",
          "States": {
            "ApiQualityCheck": {
              "Type": "Task",
              "ResultPath": "$.apiQualityCheck",
              "Resource": "${b220d0aaa385d12ba2e3fe1725179663}",
              "Retry": [
                {
                  "ErrorEquals": [
                    "States.TaskFailed"
                  ],
                  "IntervalSeconds": 10,
                  "MaxAttempts": 3
                }
              ],
              "End": true
            }
          }
        }
      ],
      "Next": "Packaging"
    },
    "Packaging": {
      "Type": "Task",
      "Resource": "${6dfb42413750d9e3618a965e029ee5c9}",
      "Retry": [
        {
          "ErrorEquals": [
            "States.TaskFailed"
          ],
          "IntervalSeconds": 10,
          "MaxAttempts": 3
        }
      ],
      "End": true
    }
  }
}
